import './App.css';
import {useState, useEffect} from "react";
import Axios from "axios";
import Header from "./components/Header"; // for http requests

function App() {

    // creating states
    const [conceptId, setConceptId] = useState(0);
    const [conceptName, setConceptName] = useState("");
    const [conceptList, setConceptList] = useState([]);

    // hook will be called whenever page will be refreshed
    useEffect(() => {
       Axios.get("http://localhost:3002/concept").then((response) => {
           setConceptList(response.data);
       })
    }, [])

    //function for adding data
    const addConcept = () => {
        // adding route from backend
        Axios.post("http://localhost:3002/insert", {
            conceptId: conceptId,
            conceptName: conceptName
        });
    };

    return (
        <div className="App">
            <Header/>
            <AppConcept/>
            <h1>Existing Concepts</h1>
            {conceptList.map((value, key) => {
                return (
                    <div>
                        <p>{value.conceptId}: {value.name}</p>
                    </div>);
            })}

        </div>
    );
}

export default App;
